import {data} from '../Data/users.js'
import { AddUserForm } from './AddUserForm/AddUserForm.js';
import {Userlist} from './User/Users'
import { nanoid } from 'nanoid';



class App extends Component   {
  state = {
    users: data,
  };


  // deleteUser = userId => {
