  addUser = (userData) => {
    const newUser = {
      id: nanoid(),
      ...userData, 
    }
    this.setState(prevState => ({users: [newUser, ...prevState.users]}))
  }
      
  render () {
    const {users} = this.state
  return (
   <div>
    {/* < Userlist users={users} deleteUser={this.deleteUser}/> */}
    < AddUserForm AddUser={this.addUser}/>
   </div>
  );
};
}

export default App;
